mapper.select:
select
r.id, r.tid, r.title, r.uid, u.name as usname, u.avatar as usavatar, r.addTime, r.likeCount, r.hateCount, r.rateCount, r.isBest,r.isBlock, r.rateCount, r.content, u.prestige as usprestige
from topic_repinfo r
left join user_basinfo u on r.uid = u.id


        id = #{id,jdbcType=INTEGER}


       and tid = #{tid,jdbcType=INTEGER}


        and title = #{title,jdbcType=VARCHAR}


       and uid = #{uid,jdbcType=VARCHAR}


       and addTime = #{addTime,jdbcType=TIMESTAMP}


        and likeCount >= #{likeCount,jdbcType=DOUBLE}


        and hateCount >= #{hateCount,jdbcType=DOUBLE}


        and rateCount >= #{rateCount,jdbcType=TINYINT}


       and isBest = #{isBest,jdbcType=BIT}


       and isBlock = #{isBlock,jdbcType=BIT}


       and rateCount = #{rateCount,jdbcType=INTEGER}


       and content = #{content,jdbcType=LONGVARCHAR}


ORDER BY r.rateCount desc

select
r.id, r.tid, r.title, r.uid, u.name as usname, u.avatar as usavatar, r.addTime, r.likeCount, r.hateCount, r.rateCount, r.isBest,r.isBlock, r.rateCount, r.content, u.prestige as usprestige
from topic_repinfo r
left join user_basinfo u on r.uid = u.id


        id = #{rep.id}


        and tid = #{rep.tid}


        and title = #{rep.title}


        and uid = #{rep.uid}


        and addTime = #{rep.addTime}


        and likeCount >= #{rep.likeCount}


        and hateCount >= #{rep.hateCount}


        and rateCount >= #{rep.rateCount}


        and isBest = #{rep.isBest}


        and isBlock = #{rep.isBlock}


        and rateCount = #{rep.rateCount}


        and content = #{rep.content}


ORDER BY r.isBest desc,r.rateCount desc
limit ${start},${end}

select
r.id, r.tid, r.title, r.uid, u.name as usname, u.avatar as usavatar, r.addTime, r.addTime, r.likeCount, r.hateCount, r.rateCount, r.isBest,r.isBlock, r.rateCount, r.content, u.prestige as usprestige
from topic_repinfo r
left join user_basinfo u on r.uid = u.id


        id = #{id,jdbcType=INTEGER}


        and tid = #{tid,jdbcType=INTEGER}


        and title = #{title,jdbcType=VARCHAR}


        and uid = #{uid,jdbcType=VARCHAR}


        and addTime = #{addTime,jdbcType=TIMESTAMP}


        and likeCount >= #{likeCount,jdbcType=DOUBLE}


        and hateCount >= #{hateCount,jdbcType=DOUBLE}


        and rateCount >= #{rateCount,jdbcType=TINYINT}


        and isBest = #{isBest,jdbcType=BIT}


        and isBlock = #{isBlock,jdbcType=BIT}


        and rateCount = #{rateCount,jdbcType=INTEGER}


        and content = #{content,jdbcType=LONGVARCHAR}


ORDER BY r.addTime desc
